defs.script:
$(window).bind( 'load', function() {
    var $root = $.__.$('#')
    var view = ( function() {
        var view = $root.attr( 'viewBox' ).split( ' ' )
        return {
            x : view[0],
            y : view[1],
            width : view[2],
            height : view[3],
        }
    } )()
    var $input = $.__.$('<html:input/>')
    $root.append( $.__.$('<foreignObject/>').attr( view )
                  .append( $input ) )
    $input.attr( 'value', 'text' )
    console.log( $input.css( 'top' ) )
} )
