defs.script:
//$( function() {
$(window).bind( 'load', function() {
  var bbox = $('#bbox').get(0).getBBox()
  if( typeof console != 'undefined' ) console.log(bbox.height)
  $.__.$('#').prepend( $.__.$('<rect/>').attr( {
    x : bbox.x, 
    y : bbox.y,
    width : bbox.width,
    height : bbox.height,
  } ) )

  var $text = $('#style')
  var style = {
    x : $text.css( 'left' ),
    y : $text.css( 'top' ),
    width : $text.width(),
    height : $text.height(),
  }
  $.__.$('#').prepend( $.__.$('<rect/>').attr( {
    x : style.x, 
    y : style.y,
    width : style.width,
    height : style.height,
  } ) )

} )
