    trigger.$root.hover(
        function() {
            hover.start = time.now
            hover.scale = 1
        },
        function() {
            hover.start = time.now
            hover.scale = -1
        } )

    var border = {
        outer: new Circle,
        inner: new Circle,
    }
    canvas.add( border.outer )
    canvas.add( border.inner )

    var band = new Circle
    canvas.add( band )

    var head = new Container( {
        id : 'head',
    } )
    head.path = new Circle( {
        path : true,
        radius : 82,
        clear : true,
    } )
    head.text = new Text( {
        text : 'Department of Happiness',
        font : 'Ragg Mopp',
        path : head.path,
        align : 'center',
        fill : 'white',
        height : 22,
    } )

    canvas.add( head )
    head.add( head.path )
    head.add( head.text )

    var sub = new Container( {
        id : 'sub',
        rotation : 180,
    } )
    sub.path = new Circle( {
        path : true,
        radius : 93,
        positive : { arc : false },
        clear : true,
    } )
    sub.text = new Text( {
        text : 'Totus Custodes',
        path : sub.path,
        font : 'Lionheart',
        align : 'center',
        fill : 'white',
        height : 22,
    } )

    canvas.add( sub )
    sub.add( sub.path )
    sub.add( sub.text )

    var rc = new Arc( {
        center : {
            x : 0,
            y : 0,
        },
        radius : 20,
        offset : 90,
        extent : 180,
    } )
    canvas.add( rc )

    var tk = new Tracker( {
        elem : rc.start,
        color : 'red',
    } )
    canvas.add( tk )
    scene.add( tk )

    tk = new Tracker( {
        elem : rc.end,
        color : 'blue',
    } )
    canvas.add( tk )
    scene.add( tk )

    ;[
        {
            elem : rc,
            attr : 'offset',
            val : slide.linear( 360 )
        },
        {
            elem : rc,
            attr : 'extent',
            val : slide.linear( 90 )
        },
        {
            elem : border.outer,
            attr : 'radius',
            val : slide.linear( 100 )
        },
        {
            elem : border.inner,
            attr : 'radius',
            val : slide.linear( 74 )
        },
        {
            elem : band,
            attr : 'radius',
            val : slide.linear( 87 )
        },
        {
            elem : band,
            attr : 'stroke-width',
            val : slide.linear( 23 )
        },
        {
            elem : border.outer,
            attr : 'stroke-width',
            val : slide.linear( 1 )
        },
        {
            elem : border.inner,
            attr : 'stroke-width',
            val : slide.linear( 1 )
        }
    ].each( function( tixel ) {
        [ 'start', 'scale', 'duration' ].each( function( attr ) {
            tixel.__defineGetter__( attr, function() { return hover[ attr ] } )
        } )
        scene.add( tixel )
    } )
} )
