function Attribute( config ) {
    var $root = config.$root || $.__.$('#')
    var attr = config.attribute || ( function() {
        throw {
            message: 'Missing Required Attribute: attribute'
        }
    } )()
    var str = $root.attr( attr ).split( ' ' )
    var ids = config.ids instanceof List && config.ids || ( function() {
        throw {
            message: 'Missing Required List: ids'
        }
    } )()

    ids.__.each( function( val, id ) {
        if( str.length > 0 ) {
            ids.__.set( id, parseFloat( str.shift() ) )
        }
    } )

    var updatable = true
    var updateʻ = config.update || function() {
        return $root.attr( attr, self.self )
    }
    var update = function() {
        return updatable && updateʻ.call( self )
    }
    this.update = update

    var self = this
    ids.__.each( function( val, prop ) {
        self.__defineGetter__( prop, function() {
            return ids.__.get( prop )
        } )
        self.__defineSetter__( prop, function( val ) {
            ids.__.set( prop, val )
            return update()
        } )
    } )

    this.__defineGetter__( 'self', function() {
        return ids.__.join( ' ' )
    } )
    this.__defineSetter__( 'self', function( config ) {
        updatable = false
        ids.__.each( function( val, id ) {
            if( config[ id ] !== undefined ) {
                self[ id ] = config[ id ]
            }
        } )
        updatable = true
        return update()
    } )

    if( config instanceof Object ) {
        this.self = config
    }
}

var view = new Attribute( {
    attribute: 'viewBox',
    ids: new List.by.ids( [ 'x', 'y', 'width', 'height' ] ),
} )
var win = { width: window.innerWidth,
            height: window.innerHeight, }
view.self = {
    x: - win.width / 2,
    y: - win.height / 2,
    width: win.width,
    height: win.height,
}

var time = {
    get now() { return ( new Date() ).getTime() }
}

function Scene() {
    var state = new List( {
        'tixel.capture.frequency.mHz' : .025 * 1000,
        'time.epoch' : time.now,
    } )
    var interval = {
        id: undefined,
    }

    var contents = new List()

    var last = time.now
    function once() {
        var now = time.now

        contents.__.each( function( tixel ) {
            var start = tixel.start || tixel.end - tixel.duration
            var end = tixel.end || start + tixel.duration
            if( ( last < start && now >= start )
                || ( last > end && now <= end )
                || ( tixel.percent > 0 && tixel.percent < 1 ) ) {
                var percent = ( now - start ) / tixel.duration
                tixel.percent = {
                    valueOf: function() { return percent },
                    get raw() { return percent },
                    get capped() { return Math.max( 0, Math.min( 1, percent ) ) },
                }
                tixel.elem[ tixel.attr ] = tixel.val.call( tixel, now - last )
            }
        } )
        last = now
    }

    this.__defineGetter__( 'once', once )

    this.__defineGetter__( 'go', function() {
        if( interval.id === undefined ) {
            var self = this
            var freq = state.__.get( 'tixel.capture.frequency.mHz' ) || 100
            interval.id =
                setInterval( function() {
                    once.apply( self, arguments )
                },
                             freq )
        }
        return this
    } )

    this.__defineGetter__( 'stop', function() {
        if( interval.id !== undefined ) {
            clearInterval( interval.id )
            interval.id = undefined
        }
        return this
    } )

    this.__defineGetter__( 'running', function() {
        return interval.id !== undefined
    } )

    this.__defineGetter__( 'pause', function() {
        return this.running ? this.stop : this.go
    } )

    this.add = function( tixel ) {
        tixel.speed = tixel.speed || 1
        contents.__.add( tixel )
    }
}

var scene = new Scene
scene.go

function Container( config ) {
    var $root = $.__.$( '<g/>' )
    $work.append( $root )

    this.__defineGetter__( 'id', function() { return $root.attr( 'id' ) } )
    this.__defineSetter__( 'id', function( id ) { return $root.attr( 'id', id ) } )

    var transform = {
        x: 0,
        y: 0,
        scale: 1,
        update: function() {
            $root.attr( 'transform',
                             'translate(' + this.x + ',' + this.y + ')'
                             + ' scale(' + this.scale + ')' )
        },
    }
    transform.update()

    var elems = []

    function add( elem ) {
        elems.push( elem )
        if( elem && elem.$root ) {
            elem = elem.$root
        }
        $root.append( elem )
    }
    this.add = add

    this.each = function( f ) {
        elems.each( f )
    }

    this.__defineGetter__( '$root', function() { return $root } )

    this.__defineGetter__( 'transform', function() { return {
        set x( x ) {
            transform.x = x
            transform.update()
        },
        // This assumes transform will not be externally set
        get x() { return transform.x },
        set y( y ) {
            transform.y = y
            transform.update()
        },
        get y() { return transform.y },
    } } )

    this.__defineSetter__( 'scale', function( scale ) { 
        transform.scale = scale
        transform.update()
    } )
}

function Label( text ) {
    var container = new Container

    var $bg = $.__.$( '<rect/>' )
    container.add( $bg )

    var $text = ( $.__.$( '<html:input/>' )
                  .attr( 'class', 'value' )
                  .attr( 'value', text ) )
    var $obj = ( $.__.$( '<foreignObject/>' )
                 .append( $text ) )
    container.add( $obj )

    this.__defineGetter__( 'width', function() {
        return ( parseFloat( $text.width() )
                 + parseFloat( $text.css( 'padding-left' ) )
                 + parseFloat( $text.css( 'padding-right' ) )
                 + parseFloat( $text.css( 'border-left-width' ) )
                 + parseFloat( $text.css( 'border-right-width' ) )
               ) } )
    this.__defineGetter__( 'height', function() {
        return ( parseFloat( $text.height() )
                 + parseFloat( $text.css( 'padding-top' ) )
                 + parseFloat( $text.css( 'padding-bottom' ) )
                 + parseFloat( $text.css( 'border-top-width' ) )
                 + parseFloat( $text.css( 'border-bottom-width' ) )
               ) } )
    this.__defineSetter__( 'x', container.transform.__lookupSetter__( 'x' ) ) 
    this.__defineGetter__( 'x', container.transform.__lookupGetter__( 'x' ) ) 
    this.__defineSetter__( 'y', container.transform.__lookupSetter__( 'y' ) ) 
    this.__defineGetter__( 'y', container.transform.__lookupGetter__( 'y' ) ) 

    this.__defineGetter__( '$root', container.__lookupGetter__( '$root' ) ) 

    this.__defineGetter__( 'text', function() { return $text.text() } )
    this.__defineSetter__( 'text', function( text ) { $text.text( text ) } )

    this.__defineGetter__( 'background', function() {
        return {
            set radius( r ) {
                $bg.attr( 'rx', r )
                $bg.attr( 'ry', r )
            }, 
        }
    } )
    this.background.radius = 4

    var size = {
        x: - this.width / 2,
        y: 0,
        width: this.width,
        height: this.height,
    }
    $obj.attr( size )
    $bg.attr( size )
}

var slide = {
    linear: function( begin, end ) {
        if( end === undefined ) {
            end = begin
            begin = undefined
        }
        return function( deltaTime ) {
            if( begin === undefined ) {
                begin = this.elem[ this.attr ]
            }
            // The transition is rate based rather than percentage based
            //   in an attempt to prevent jumps when transitions are
            //   joined unexpectedly.
            // 
            this.last = this.last || {}
            this.last.value = ( this.last.value === undefined
                                ? this.elem[ this.attr ]
                                : this.last.value )
            var scale = this.scale || 1
            this.last.value += ( scale * ( end - begin ) * deltaTime / this.duration )
            this.last.value = Math.max( Math.min( begin, end ), this.last.value )
            this.last.value = Math.min( Math.max( begin, end ), this.last.value )
            return this.last.value
        }
    },
}

function Value( val, id ) {
    var container = new Container
    this.__defineGetter__( '$root', container.__lookupGetter__( '$root' ) ) 
    this.__defineSetter__( 'x', container.transform.__lookupSetter__( 'x' ) ) 
    this.__defineGetter__( 'x', container.transform.__lookupGetter__( 'x' ) ) 
    this.__defineSetter__( 'y', container.transform.__lookupSetter__( 'y' ) ) 
    this.__defineGetter__( 'y', container.transform.__lookupGetter__( 'y' ) ) 

    var hover = {
        start: false,
        duration: .5 * 1000,
    }

    if( typeof val == 'function' ) {
        var id = new Label( id )
        container.add( id )
        this.__defineGetter__( 'width', id.__lookupGetter__( 'width' ) )
        this.__defineGetter__( 'height', id.__lookupGetter__( 'height' ) )

        container.$root.click( val )                
    } else {
        var value = new Label( val )
        var id = new Label( id )
        container.add( value )
        container.add( id )
        this.__defineGetter__( 'width', function() {
            return Math.max( value.width, id.width )
        } )
        this.__defineGetter__( 'height', function() {
            return Math.max( value.height, id.height )
        } )

        scene.add( {
            elem: value,
            attr: 'x',
            get start() { return hover.start },
            get scale() { return hover.scale },
            get duration() { return hover.duration },
            val: slide.linear( value.x, value.x - value.width / 2 )
        } )
        scene.add( {
            elem: id,
            attr: 'x',
            get start() { return hover.start },
            get scale() { return hover.scale },
            get duration() { return hover.duration },
            val: slide.linear( id.x + id.width / 2 )
        } )
    }

    container.$root.hover(
        function() {
            hover.start = time.now
            hover.scale = 1
        },
        function() {
            hover.start = time.now
            hover.scale = -1
        } )
}

var pad = { top: 5, between: 5, bottom: 5, left: 5, right: 5 }
var display = {
    JSON: function( list ) {
        var lines = []
        var container = new Container

        list.__.each( function( line, id ) {
            var val = new Value( line, id )
            container.add( val )
        } )
        var y = pad.top
        container.each( function( line ) {
            line.y = y
            y += line.height + pad.between
        } )
        y -= pad.between - pad.bottom
        container.transform.y = - y / 2

        return container
    },
}

function Circle( config ) {
    config = config || {}
    var $root = ( config.path
                  ? $.__.$( '<path/>' ).attr( {
                      d : '',
                  } )
                  : $.__.$( '<circle/>' ).attr( {
                      cx : 0,
                      cy : 0,
                      r : 0,
                  } ) )

    var curve = {
        radius : 0,
        center : {
            x : 0,
            y : 0,
        },
        x : { axis : { rotation : 0 } },
        large : { arc : true },
        positive : { arc : true },
        update : function() {
            $root.attr( 'd', [
                'm',
                this.center.x - this.radius,
                this.center.y,
                'a', this.radius, this.radius,
                this.x.axis.rotation,
                this.large.arc ? 1 : 0,
                this.positive.arc ? 1 : 0,
                this.center.x + this.radius * 2,
                this.center.y,
                'a', this.radius, this.radius,
                this.x.axis.rotation,
                this.large.arc ? 1 : 0,
                this.positive.arc ? 1 : 0,
                this.center.x - this.radius * 2,
                this.center.y
            ].join( ' ' ) )
        }
    }

    var center = ( config.path
                   ? {
                       get x() { return curve.center.x },
                       set x( x ) {
                           curve.center.x = x;
                           return curve.update.call( curve )
                       },
                       get y() { return curve.center.y },
                       set y( x ) {
                           curve.center.y = y;
                           return curve.update.call( curve )
                       },
                   }
                    : {
                        get x() { return parseFloat( $root.attr( 'cx' ) ) },
                        set x( x ) { return $root.attr( 'cx', x ) },
                        get y() { return parseFloat( $root.attr( 'cy' ) ) },
                        set y( y ) { return $root.attr( 'cy', y ) },
                    } )

    var circle = {
        get center() { return center },
        set center( c ) {
            if( c.x !== undefined ) {
                center.x = c.x
            }
            if( c.y !== undefined ) {
                center.y = c.y
            }
            return center
        },
    }
    circle.__defineGetter__( 'radius', ( config.path
                                         ? function() { return curve.radius }
                                         : function() { return parseFloat( $root.attr( 'r' ) ) }
                                       ) )
    circle.__defineSetter__( 'radius', ( config.path
                                         ? function( radius ) {
                                             curve.radius = radius
                                             return curve.update.call( curve )
                                         }
                                         : function( radius ) { $root.attr( 'r', radius ) }
                                       ) )

    var self = this
    for( prop in circle ) {
        var get = circle.__lookupGetter__( prop )
        var set = circle.__lookupSetter__( prop )
        if( get || set ) {
            if( get ) {
                self.__defineGetter__( prop, get )
            }
            if( set ) {
                self.__defineSetter__( prop, set )
            }
        } else {
            self[ prop ] = circle[ prop ]
        }
    }

    this.__defineGetter__( '$root', function() { return $root } )

    this.__defineGetter__( 'id', function() { return $root.attr( 'id' ) } )
    this.__defineSetter__( 'id', function( id ) { return $root.attr( 'id', id ) } )

    ;[ 'stroke-width' ].each( function( attr ) {
        self.__defineGetter__( attr, function() {
            return parseFloat( $root.css( attr ) )
        } )
        self.__defineSetter__( attr, function( val ) {
            //$root.css( attr, val )
            $root.get(0).style.setProperty( attr, val, null )
        } )
    } )

    ;[ 'stroke', 'fill' ].each( function( attr ) {
        self.__defineGetter__( attr, function() {
            return $root.css( attr )
        } )
        self.__defineSetter__( attr, function( val ) {
            $root.get(0).style.setProperty( attr, val, null )
        } )
    } )

    if( config instanceof Object ) {
        for( prop in self ) {
            if( config[ prop ] !== undefined ) {
                self[ prop ] = config[ prop ]
            }
        }
    }
}

function Text() {
    var $path
    var $root = ( $.__.$( '<text/>' )
                  .append( $text )
                  .attr( {
                      x : 0,
                      y : 0,
                  } ) )
    var $text = $root

    this.__defineGetter__( '$root', function() { return $root } )

    this.__defineGetter__( 'text', function() { return $text.text() } )
    this.__defineSetter__( 'text', function( text ) { $text.text( text ) } )

    function align( position ) {
        return $root.get(0).style.setProperty( 'text-anchor', position, null )
    }
    this.__defineGetter__( 'align', function() { return {
        get center() { return align( 'middle' ) },
        get start() { return align( 'start' ) },
        get end() { return align( 'end' ) },
    } } )

    this.__defineSetter__( 'path', function( path ) {
        $path = $path || ( function() {
            var $path = $text = $.__.$( '<textPath/>' )
            $path.text( $root.text() )
            $root.text( '' )
            $root.append( $path )

            return $path
        } )()
        $path.get(0).setAttributeNS( $.__.$.ns[ 'xlink' ], 'xlink:href', path )
    } )

    var self = this
    if( config instanceof Object ) {
        for( prop in self ) {
            if( config[ prop ] !== undefined ) {
                self[ prop ] = config[ prop ]
            }
        }
    }
}

function Arc() {
